% Parametros para los metodosleft = 0;right = 0.15;ya = 0;% Parametros para la funcion y su derivadaa = 5000/2501;b = 100/2501;% F sin derivarfun = @(t) (a*exp(-t/2)) + (b*sin(25*t)) - (a*cos(25*t));% Derivada de ffun_prime = @(t, yt) (-0.5 * yt) + 50 * sin(25 * t);% METODO RK4% Listas para los h y sus erroresh_val = [];e_val = [];t_val = [];for j=1:12  h = right/pow2(j);  h1 = right/pow2(j + 1);  [x, u] = rk4(fun_prime, left, right, ya, pow2(j), h);  [x1, u1] = rk4(fun_prime, left, right, ya, pow2(j + 1), h1);  h_val = [h_val h];  e_val = [e_val diff(u, u1(1:2:end))];  % Desde el primero al ultimo con step de 2  t_val = [t_val u1(end)];end% Valores correspondientes a h y el error que se obtienedisp('RK4 Table(h - Maximum Difference between h and h/2 - fun(T)):')vals = [h_val; e_val; t_val]'% METODO RK3% Listas para los h y sus erroresh_val = [];e_val = [];t_val = [];for j=1:12  h = right/pow2(j);  h1 = right/pow2(j + 1);  [x, u] = rk3(fun_prime, left, right, ya, pow2(j), h);  [x1, u1] = rk3(fun_prime, left, right, ya, pow2(j + 1), h1);  h_val = [h_val h];  e_val = [e_val diff(u, u1(1:2:end))];  % Desde el primero al ultimo con step de 2  t_val = [t_val u1(end)];end% Valores correspondientes a h y el error que se obtienedisp('RK3 Table(h - Maximum Difference between h and h/2 - fun(T)):')vals = [h_val; e_val; t_val]'