% Parametros para los metodosleft = 0;right = 0.15;ya = 0;% Parametros para la funcion y su derivadaa = 5000/2501;b = 100/2501;% F sin derivarfun = @(t) (a*exp(-t/2)) + (b*sin(25*t)) - (a*cos(25*t));% Derivada de ffun_prime = @(t, yt) (-0.5 * yt) + 50 * sin(25 * t);% METODO RK4% Listas para los h y sus errorese = 1;h_val = [];e_val = [];j = 1;n = 2;while e >= power(10, -6)  h = right/n;  [x, u] = rk4(fun_prime, left, right, ya, n, h);  [x1, u1] = rk4(fun_prime, right, left, u(end), n, -h);  e = my_diff(u1(end), ya);  h_val = [h_val h];  e_val = [e_val e];  j = j + 1;  n = n * 2;endwhiledisp('RK4 Table(h - Maximum Difference between ya and aproximation')vals = [h_val; e_val]'